import React, {useState} from 'react';
import { StatusBar } from 'expo-status-bar';

//formik for our forms
import {Formik} from 'formik';

//icons
import {Octicons, Ionicons, Fontisto} from '@expo/vector-icons';


import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    SubTitle,
    StyledFormArea,
    LeftIcon,
    StyledInputLabel,
    StyledTextInput,
    RightIcon,
    StyledButton,
    ButtonText,
    Colors,
    MsgBox,
    Line,
    ExtraView,
    ExtraText,
    TextLink,
    TextLinkContent,
} from './../components/styles';
import {View } from 'react-native';

//colors
const {brand, darkLight, primary} = Colors;

//keyboard avaoiding view
import KeyboardAvoidingWrapper from './../components/KeyboardAvoidingWrapper';

//axios
import axios from 'axios';


const Login = ({navigation}) => {
    const [hidePassword, setHidePassword] = useState(true)
    //state varible to handle message and message type.
    const [message, setMessage] = useState();
    const [messageType, setMessageType] = useState();

    const handleLogin = (credentials) => {
        const url = 'http://localhost:5000/user/signin'
    axios.post(url, credentials)
    .then((response)=>{
        const result = response.data;
        const {message, status, data} = result;
    }).catch(error=>{
        console.log(error.JSON());
    })
    }

    //function to handle the message
    const handleMessage = (message, type = 'FAILED') => {
        setMessage(message);
        setMessageType(type);
    }

    return (
        <KeyboardAvoidingWrapper>
        <StyledContainer>
            <StatusBar style="dark"/>
            <InnerContainer>
                <PageLogo resizeMode="cover" />
                <PageTitle>Escrosis</PageTitle>
                <SubTitle>Account Login</SubTitle>

                <Formik
                     initialValues={{email: '', password: ''}}
                     onSubmit={(values) => {
                         console.log(values);
                         navigation.navigate("Welcome");
                     }}
                >
                    {({handleChange, handleBlur, handleSubmit, values}) => (
                    <StyledFormArea>
                        <MyTextInput 
                            label = "Email Address"
                            icon = "mail"
                            placeholder="mosesegboh@gmail.com"
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('email')}
                            onBlur = {handleBlur('email')}  
                            value={values.email}
                            keyboardType = "email-address"
                        />

                        <MyTextInput 
                            label = "Password"
                            icon = "lock"
                            placeholder="* * * * * * "
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('password')}
                            onBlur = {handleBlur('password')}  
                            value={values.password}
                            secureTextEntry={hidePassword}
                            isPassword = {true}
                            hidePassword = {hidePassword}
                            setHidePassword = {setHidePassword}
                        />
                        <MsgBox type={messageType}>{message}</MsgBox>
                        <StyledButton onPress={handleSubmit}>
                            <ButtonText>
                                Login
                            </ButtonText>
                        </StyledButton>
                        <Line />
                        <StyledButton google={true} onPress={handleSubmit}>
                            <Fontisto name="google" color="primary" size={25}/>
                            <ButtonText google={true}>
                                Sign In With Google
                            </ButtonText>
                        </StyledButton>

                        <ExtraView>
                            <ExtraText>Don't have an account already? </ExtraText>
                            <TextLink onPress = {()=>navigation.navigate("SignUp")}>
                                <TextLinkContent>Sign Up</TextLinkContent>
                            </TextLink>
                        </ExtraView>
                    </StyledFormArea>) }
                </Formik>
            </InnerContainer>
        </StyledContainer>
        </KeyboardAvoidingWrapper>
    );
};

const MyTextInput = ({label, icon,hidePassword,isPassword, setHidePassword, ...props}) => {
    return (
        <View>
            <LeftIcon>
                <Octicons name={icon} size={30} color={brand}/>
            </LeftIcon>
            <StyledInputLabel>{label}</StyledInputLabel>
            <StyledTextInput {...props} />
            {isPassword && (
                <RightIcon onPress = {() => setHidePassword(!hidePassword)} > 
                    <Ionicons name={hidePassword ? 'md-eye-off' : 'md-eye'} size={30} color={darkLight}  />
                </RightIcon>
            )}
        </View>
    );
};

export default Login;
