import React, {useState, useRef} from 'react';
import {
    Text,
  View,
  StyleSheet,
  Image,
  Dimensions,
  TouchableWithoutFeedback,
  TouchableOpacity,
  Animated,
  FlatList
} from 'react-native';

//install these packages
import SlidingUpPanel from 'rn-sliding-up-panel'
import Carousel from 'react-native-snap-carousel'

const Home = () => {
  return (
      <View style={styles.container}>
          <View style={{paddingTop: 50, paddingHorizontal: 14}}>
            <View style={{flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center'}}>
              <View>
                <Text style={{fontSize: 26, color: '#fff'}}>Welcome back,</Text>
                <Text style={{fontSize: 26, color: '#fff', opacity: 0.6}}> Moses Nkem</Text>
              </View>
              <View>
                <Image 
                  source={{uri: 'https://images.pexels.com/photos/936229/pexels-photo-936229.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260'}}
                  style={styles.ProfileImage}
                />
              
              </View>

            </View>
          </View>
      </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#000',
      paddingTop: 0
    },
    ProfileImage: {
      width: 55,
      height: 55,
      borderRadius: 40,
    }
});

export default Home;
