import React, {useState} from 'react';
import { StatusBar } from 'expo-status-bar';

//formik for our forms
import {Formik} from 'formik';

//icons
import {Octicons, Ionicons, Fontisto} from '@expo/vector-icons';


import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    SubTitle,
    StyledFormArea,
    LeftIcon,
    StyledInputLabel,
    StyledTextInput,
    RightIcon,
    StyledButton,
    ButtonText,
    Colors,
    MsgBox,
    Line,
    ExtraView,
    ExtraText,
    TextLink,
    TextLinkContent,
} from './../components/styles';
import {View } from 'react-native';

//colors
const {brand, darkLight, primary} = Colors;

const SignUp = () => {
    const [hidePassword, setHidePassword] = useState(true)

    return (
        <StyledContainer>
            <StatusBar style="dark"/>
            <InnerContainer>
                {/* <PageLogo resizeMode="cover" /> */}
                <PageTitle>Escrosis</PageTitle>
                <SubTitle>Account SignUp</SubTitle>

                <Formik
                     initialValues={{fullName: '',email: '', dateoOfBirth: '', password: '', confirmPassword: ''}}
                     onSubmit={(values) => {
                         console.log(values);
                     }}
                >
                    {({handleChange, handleBlur, handleSubmit, values}) => (
                    <StyledFormArea>
                        <MyTextInput 
                            label = "Full Name"
                            icon = "person"
                            placeholder="Egboh Moses"
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('fullName')}
                            onBlur = {handleBlur('fullName')}  
                            value={values.fullName}
                        />

                        <MyTextInput 
                            label = "Email Address"
                            icon = "mail"
                            placeholder="mosesegboh@gmail.com"
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('email')}
                            onBlur = {handleBlur('email')}  
                            value={values.email}
                            keyboardType = "email-address"
                        />

                        <MyTextInput 
                            label = "Date Of Birth"
                            icon = "calendar"
                            placeholder="YYYY-MM-DD"
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('dateoOfBirth')}
                            onBlur = {handleBlur('dateoOfBirth')}  
                            value={values.dateoOfBirth}
                            
                        />

                        <MyTextInput 
                            label = "Password"
                            icon = "lock"
                            placeholder="* * * * * * "
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('password')}
                            onBlur = {handleBlur('password')}  
                            value={values.password}
                            secureTextEntry={hidePassword}
                            isPassword = {true}
                            hidePassword = {hidePassword}
                            setHidePassword = {setHidePassword}
                        />

                        <MyTextInput 
                            label = "Confirm Password"
                            icon = "lock"
                            placeholder="* * * * * * "
                            placeholderTextColor = {darkLight}
                            onChangeText = {handleChange('confirmPassword')}
                            onBlur = {handleBlur('confirmPassword')}  
                            value={values.confirmPassword}
                            secureTextEntry={hidePassword}
                            isPassword = {true}
                            hidePassword = {hidePassword}
                            setHidePassword = {setHidePassword}
                        />

                        <MsgBox>...</MsgBox>
                        <StyledButton onPress={handleSubmit}>
                            <ButtonText>
                                Login
                            </ButtonText>
                        </StyledButton>
                        <Line />

                        <ExtraView>
                            <ExtraText>Already have an account </ExtraText>
                            <TextLink>
                                <TextLinkContent>Log In</TextLinkContent>
                            </TextLink>
                        </ExtraView>
                    </StyledFormArea>) }
                </Formik>
            </InnerContainer>
        </StyledContainer>
    );
};

const MyTextInput = ({label, icon,hidePassword,isPassword, setHidePassword, ...props}) => {
    return (
        <View>
            <LeftIcon>
                <Octicons name={icon} size={30} color={brand}/>
            </LeftIcon>
            <StyledInputLabel>{label}</StyledInputLabel>
            <StyledTextInput {...props} />
            {isPassword && (
                <RightIcon onPress = {() => setHidePassword(!hidePassword)} > 
                    <Ionicons name={hidePassword ? 'md-eye-off' : 'md-eye'} size={30} color={darkLight}  />
                </RightIcon>
            )}
        </View>
    );
};

export default SignUp;
