import React from 'react';
import { View, SafeAreaView, Button, StyleSheet, ScrollView } from 'react-native';
import {
    Avatar,
    Title,
    Caption,
    Text,
    TouchableRipple,
  } from 'react-native-paper';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const Profile = () => {
    return (
        <SafeAreaView style={styles.container}>
            <ScrollView>
            <View style={styles.userInfoSection}>
                <View style={styles.avi}>
                    <Avatar.Image 
                        source = {{
                            uri: 'https://api.adorable.io/avatars/80/abott@adorable.png',
                        }}
                        size={80}
                    />
                    <View style={{marginLeft: 20}}>
                        <Title style={[styles.title, {
                            marginTop: 15,
                            marginBottom: 5,
                        }]}>Egboh Moses</Title>
                        <Caption style={styles.caption}>@moses</Caption>
                    </View>
                </View>
            </View>

            <View style={styles.userInfoSection}>
                <View style={styles.row}>
                    <Icon name="map-marker-radius" color="#777777" size={20} />
                    <Text style={{color: "#777777", marginLeft: 20}}>Lagos Nigeria</Text>
                </View>
                <View style={styles.row}>
                    <Icon name="phone" color="#777777" size={20} />
                    <Text style={{color: "#777777", marginLeft: 20}}>+234 7030163523</Text>
                </View>
                <View style={styles.row}>
                    <Icon name="email" color="#777777" size={20} />
                    <Text style={{color: "#777777", marginLeft: 20}}>mosesegboh@yahoo.com</Text>
                </View>
            </View>


            <View style={styles.infoBoxWrapper}>
                <View style={[styles.infoBox, {
                    borderRightColor: '#dddddd',
                    borderRightWidth: 1
                }]}>
                    <Title>$140</Title>
                    <Caption>Wallet</Caption>
                </View>

                <View style={styles.infoBox}>
                    <Title>12</Title>
                    <Caption>Total Transaction</Caption>
                </View>
            </View>

            <View style={styles.menuWrapper}>
                <TouchableRipple onPress ={() => {}}>
                    <View style={styles.menuItem}>
                        <Icon name="heart" color="#FF6347" size={25}/>
                        <Text style={styles.menuItemText}>Your Favorites</Text>
                    </View>
                </TouchableRipple>
                <TouchableRipple onPress ={() => {}}>
                    <View style={styles.menuItem}>
                        <Icon name="credit-card" color="#FF6347" size={25}/>
                        <Text style={styles.menuItemText}>Payments</Text>
                    </View>
                </TouchableRipple>
                <TouchableRipple onPress ={() => {}}>
                    <View style={styles.menuItem}>
                        <Icon name="share-outline" color="#FF6347" size={25}/>
                        <Text style={styles.menuItemText}>Tell Your Friends</Text>
                    </View>
                </TouchableRipple>
                <TouchableRipple onPress ={() => {}}>
                    <View style={styles.menuItem}>
                        <Icon name="account-check-outline" color="#FF6347" size={25}/>
                        <Text style={styles.menuItemText}>Support</Text>
                    </View>
                </TouchableRipple>
                <TouchableRipple onPress ={() => {}}>
                    <View style={styles.menuItem}>
                        <Icon name="share" color="#FF6347" size={25}/>
                        <Text style={styles.menuItemText}>Settings</Text>
                    </View>
                </TouchableRipple>
            </View>
            </ScrollView>
        </SafeAreaView>
    );
};

export default Profile;

const styles = StyleSheet.create ({
    container: {
        flex: 1,
        // alignItems: 'center',
        // justifyContent: 'center'
    },
    avi: {
        paddingTop: 70,
        flexDirection: 'row',
        marginTop: 15
    },
    userInfoSection: {
        paddingHorizontal: 30,
        marginBottom: 25,
      },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
      },
    caption: {
        fontSize: 14,
        lineHeight: 14,
        fontWeight: '500',
      },
    row: {
        flexDirection: 'row',
        marginBottom: 10,
      },
    infoBoxWrapper: {
        borderBottomColor: '#dddddd',
        borderBottomWidth: 1,
        borderTopColor: '#dddddd',
        borderTopWidth: 1,
        flexDirection: 'row',
        height: 100,
      },
    infoBox: {
        width: '50%',
        alignItems: 'center',
        justifyContent: 'center',
      },
    menuWrapper: {
        marginTop: 10,
    },
    menuItem: {
        flexDirection: 'row',
        paddingVertical: 15,
        paddingHorizontal: 30,
    },
    menuItemText: {
        color: '#777777',
        marginLeft: 20,
        fontWeight: '600',
        fontSize: 16,
        lineHeight: 26,
    },
});
